const Listado = ({data, sumar}) => { 
  // 
  const items = getItems() 


  return(
    <div className='container'>
    {
      /* data.map( (item) => (<Item key={item.id} nombre={item.producto.nombre} descripcion={item.producto.descripcion} stock={item.stock} sumarTotal={sumar} />) )*/
      items.map( (item) => (<Item key={item.id} nombre={item.producto.nombre} descripcion={item.producto.descripcion} stock={item.stock} sumarTotal={sumar} />) )

    
    }
    </div>
  )
}
export default Listado
