// PROPS: Item recibe todos los campos que muestra en pantalla: nombre, descripcion, stock y el métodos heredados para su uso.
// Maqueta de Item:
//    h3
//    p
//    h5 > span    (este span debe mostrar la cantidad si es mayor a 0 "agotado" si llega a 0)
//    button       (este boton debe permitir comprar, pero si la cantidad es menor a 0 debe estar deshabilitado y decir "Sin stock")


export default function Item(nombre, descripcion, stock, actualizar) {
  // ni idea si es asi esto:
  // cantStock = stock > 0 ? <span>{stock}</span> : <span>agotado</span>
  // boton = stock > 0 ? <button>COMPRAR</button> : <button disabled>Sin stock</button>

  return (
    <div className='producto'>
      {/* maquetar Item aquí */}
      <h3>{nombre}</h3>
      <p>{descripcion}</p>
      <h5>En stock: <span>{}</span></h5>
      <button onClick={(e) => handleClick(e)} >COMPRAR</button>
    </div>
  )
}
