import React from 'react'
import Item from './Item'
import '../index.css'

// Debemos importar el listado de objetos del archivo JSON para usarlos como array a la hora de crear cada Item.
// El componente Listado es el padre de:
// - varios Item
// ESTADO: Listado no necesita manejar un estado.
// MÉTODOS: Listado no requiere de métodos.
// PROPS: Listado recibe el método para aumentar el estado de App y se lo pasa a cada uno de sus hijos.

export default function Listado(data, sumar, restar) {
  return (
    <div className='container'>
      {/* renderizamos los Item aquí */
      data.map( (item) => (<Item key={item.id} nombre={item.nombre} descripcion={item.descripcion} stock={item.stock} sumar restar/>) )
            // no se si es así, o es item.producto.nombre y item.producto.descripcion
      }
    </div>
  )
}
