// Debemos importar el listado de objetos del archivo JSON para usarlos como array a la hora de crear cada Item.
// El componente Listado es el padre de:
// - varios Item
// ESTADO: Listado no necesita manejar un estado.
// MÉTODOS: Listado no requiere de métodos.
// PROPS: Listado recibe el método para aumentar el estado de App y se lo pasa a cada uno de sus hijos.
import React from 'react'
import Item from './Item'
import { getItems } from './data'

const jsonData = require('./data.json');
const itemsData = JSON.parse(jsonData);
// const itemData = JSON.parse(jsonData);
//console.log(itemData);
// const arrItemData = Object.keys(itemData).map(key => itemData[key])


const Listado = ({sumar}) => { 
  // const items = getItems() 


  return(
    <div className='container'>
    {
      /* items.map( (item) => (<Item key={item.id} nombre={item.producto.nombre} descripcion={item.producto.descripcion} stock={item.stock} sumarTotal={sumar} />) )*/
      itemsData.map( (item) => (<Item key={item.id} nombre={item.producto.nombre} descripcion={item.producto.descripcion} stock={item.stock} sumarTotal={sumar} />) )

    
    }
    </div>
  )
}
export default Listado
