// El componente Item no tiene componentes hijos.
// ESTADO: Item debe tener un número para almacenar la cantidad de stock, la misma se la defina el padre a la hora de crearlo.
// MÉTODOS: Item debe manejar el click de su boton para restar la cantidad en stock de sí mismo y a su vez poder aumentar el estado de su "abuelo" App.
// PROPS: Item recibe todos los campos que muestra en pantalla: nombre, descripcion, stock y el métodos heredados para su uso.
// Maqueta de Item:
//    h3
//    p
//    h5 > span    (este span debe mostrar la cantidad si es mayor a 0 "agotado" si llega a 0)
//    button       (este boton debe permitir comprar, pero si la cantidad es menor a 0 debe estar deshabilitado y decir "Sin stock")
import { useState } from 'react'

const Item = ({nombre, descripcion, stock, sumarTotal}) => { 
  
  const [counterStock, setCounterStock] = useState(stock)
  const handlerRestar = () => { 
    setCounterStock(counterStock - 1)
  }

  /*
  {counterStock > 0 ? (
      <h5>En stock: {counterStock}</h5>, 
      <button onClick={handlerRestar}>COMPRAR</button> 
      ) : ( 
      <h5>En stock: <span>agotado</span></h5>,
      <button disabled>Sin stock</button>
      )}
  */

 // <button onClick={sumarTotal}>COMPRAR</button>

  if (counterStock > 0) {
  <button onClick={handlerRestar && sumarTotal}> if COMPRAR</button>
} else { 
  <button disabled>if Sin stock</button>}


  return(
    <div className='producto'>
      <h3>{nombre}</h3>
      <p>{descripcion}</p>
      {counterStock > 0 ? <h5>En stock: {counterStock}</h5> : <h5>En stock: <span>agotado</span></h5>}
      
      
      {counterStock > 0 ? <button onClick={handlerRestar}> restar COMPRAR</button> : <button disabled>Sin stock</button>}
      {counterStock > 0 ? <button onClick={sumarTotal}>sumar COMPRAR</button> : <button disabled>Sin stock</button>}
      </div>
  )
}
export default Item
